        /******************************************************
﻿  ﻿  ﻿  ﻿  data storage and retrival
﻿   ******************************************************/
        /* load cart from cookie */
        me.load = function () {
          var me = this;
          /* initialize variables and items array */
          me.items = {};
          me.total = 0.00;
          me.quantity = 0;
          /* retrieve item data from cookie */
          if( readCookie('simpleCart') ){
            var data = unescape(readCookie('simpleCart')).split('++');
            for(var x=0, xlen=data.length;x<xlen;x++){
              var info = data[x].split('||');
              var newItem = new CartItem();
              if( newItem.parseValuesFromArray( info ) ){
                newItem.checkQuantityAndPrice();
                /* store the new item in the cart */
                me.items[newItem.id] = newItem;
              }
            }
          }
          me.isLoaded = true;
        };
        /* save cart to cookie */
        me.save = function () {
          var dataString = "";
          for( var item in this.items ){
            dataString = dataString + "++" + this.items[item].print();
          }
          createCookie('simpleCart', dataString.substring( 2 ), 30 );
        };
        /******************************************************
﻿  ﻿  ﻿  ﻿   view management
﻿   ******************************************************/
        me.initializeView = function() {
          var me = this;
          me.totalOutlets ﻿  ﻿  ﻿  = getElementsByClassName('simpleCart_total');
          me.quantityOutlets ﻿  ﻿  ﻿  = getElementsByClassName('simpleCart_quantity');
          me.cartDivs ﻿  ﻿  ﻿  ﻿  = getElementsByClassName('simpleCart_items');
          me.taxCostOutlets﻿  ﻿  ﻿  = getElementsByClassName('simpleCart_taxCost');
          me.taxRateOutlets﻿  ﻿  ﻿  = getElementsByClassName('simpleCart_taxRate');
          me.shippingCostOutlets﻿  ﻿  = getElementsByClassName('simpleCart_shippingCost');
          me.finalTotalOutlets﻿  ﻿  = getElementsByClassName('simpleCart_finalTotal');
          me.addEventToArray( getElementsByClassName('simpleCart_checkout') , simpleCart.checkout , "click");
          me.addEventToArray( getElementsByClassName('simpleCart_empty') ﻿  , simpleCart.empty , "click" );
          me.Shelf.readPage();
          me.pageIsReady = true;
        };
        me.updateView = function() {
          me.updateViewTotals();
          if( me.cartDivs && me.cartDivs.length > 0 ){
            me.updateCartView();
          }
        };
        me.updateViewTotals = function() {
          var outlets = [ ["quantity"﻿  ﻿  , "none"﻿  ﻿  ] ,
                         ["total"﻿  ﻿  , "currency"﻿  ] ,
                         ["shippingCost"﻿  , "currency"﻿  ] ,
                         ["taxCost"﻿  ﻿  , "currency"﻿  ] ,
                         ["taxRate"﻿  ﻿  , "percentage"﻿  ] ,
                         ["finalTotal"﻿  , "currency"﻿  ] ];
          for( var x=0,xlen=outlets.length; x<xlen;x++){
            var arrayName = outlets[x][0] + "Outlets",
                outputString;
            for( var element in me[ arrayName ] ){
              switch( outlets[x][1] ){
                case "none":
                  outputString = "" + me[outlets[x][0]];
                  break;
                case "currency":
                  outputString = me.valueToCurrencyString( me[outlets[x][0]] );
                  break;
                case "percentage":
                  outputString = me.valueToPercentageString( me[outlets[x][0]] );
                  break;
                default:
                  outputString = "" + me[outlets[x][0]];
                  break;
              }
              me[arrayName][element].innerHTML = "" + outputString;
            }
          }
        };
        me.updateCartView = function() {
          var newRows = [],
              x,newRow,item,current,header,newCell,info,outputValue,option,headerInfo;
          /* create headers row */
          newRow = document.createElement('div');
          for( header in me.cartHeaders ){
            newCell = document.createElement('div');
            headerInfo = me.cartHeaders[header].split("_");
            newCell.innerHTML = headerInfo[0];
            newCell.className = "item" + headerInfo[0];
            for(x=1,xlen=headerInfo.length;x<xlen;x++){
              if( headerInfo[x].toLowerCase() == "noheader" ){
                newCell.style.display = "none";
              }
            }
            newRow.appendChild( newCell );
          }
          newRow.className = "cartHeaders";
          newRows[0] = newRow;
          /* create a row for each item in the cart */
          x=1;
          for( current in me.items ){
            newRow = document.createElement('div');
            item = me.items[current];
            for( header in me.cartHeaders ){
              newCell = document.createElement('div');
              info = me.cartHeaders[header].split("_");
              switch( info[0].toLowerCase() ){
                case "total":
                  outputValue = me.valueToCurrencyString(parseFloat(item.price)*parseInt(item.quantity,10) );
                  break;
                case "increment":
                  outputValue = me.valueToLink( "+" , "javascript:;" , "onclick=\"simpleCart.items[\'" + item.id + "\'].increment();\"" );
                  break;
                case "decrement":
                  outputValue = me.valueToLink( "-" , "javascript:;" , "onclick=\"simpleCart.items[\'" + item.id + "\'].decrement();\"" );
                  break;
                case "remove":
                  outputValue = me.valueToLink( "x" , "javascript:;" , "onclick=\"simpleCart.items[\'" + item.id + "\'].remove();\"" );
                  break;
                case "price":
                  outputValue = me.valueToCurrencyString( item[ info[0].toLowerCase() ] ? item[info[0].toLowerCase()] : " " );
                  break;
                default:
                  outputValue = item[ info[0].toLowerCase() ] ? item[info[0].toLowerCase()] : " ";
                  break;
              }﻿  
              for( var y=1,ylen=info.length;y<ylen;y++){
                option = info[y].toLowerCase();
                switch( option ){
                  case "image":
                  case "img":
                    outputValue = me.valueToImageString( outputValue );﻿  ﻿  
                    break;
                  case "input":
                    outputValue = me.valueToTextInput( outputValue , "onchange=\"simpleCart.items[\'" + item.id + "\'].set(\'" + outputValue + "\' , this.value);\""  );
                    break;
                  case "div":
                  case "span":
                  case "h1":
                  case "h2":
                  case "h3":
                  case "h4":
                  case "p":
                    outputValue = me.valueToElement( option , outputValue , "" );
                    break;
                  case "noheader":
                    break;
                  default:
                    error( "unkown header option: " + option );
                    break;
                }
              }﻿  ﻿    
              newCell.innerHTML = outputValue;
              newCell.className = "item" + info[0];
              newRow.appendChild( newCell );
            }﻿  ﻿  ﻿  
            newRow.className = "itemContainer";
            newRows[x] = newRow;
            x++;
          }
          for( current in me.cartDivs ){
            /* delete current rows in div */
            var div = me.cartDivs[current];
            while( div.childNodes[0] ){
              div.removeChild( div.childNodes[0] );
            }
            for(var j=0, jLen = newRows.length; j<jLen; j++){
              div.appendChild( newRows[j] );
            }
          }
        };
        me.addEventToArray = function ( array , functionCall , theEvent ) {
          for( var outlet in array ){
            var element = array[outlet];
            if( element.addEventListener ) {
              element.addEventListener(theEvent, functionCall , false );
            } else if( element.attachEvent ) {
              element.attachEvent( "on" + theEvent, functionCall );
            }
          }
        };
        me.createHiddenElement = function ( name , value ){
          var element = document.createElement("input");
          element.type = "hidden";
          element.name = name;
          element.value = value;
          return element;
        };
        /******************************************************
﻿  ﻿  ﻿  ﻿  Currency management
﻿   ******************************************************/
        me.currencySymbol = function() {﻿  ﻿  
          switch(me.currency){
            case JPY:
              return "&yen;";
            case EUR:
              return "&euro;";
            case GBP:
              return "&pound;";
            case USD:
            case CAD:
            case AUD:
            case NZD:
            case HKD:
            case SGD:
              return "&#36;";
            default:
              return "";
          }
        };
        me.currencyStringForPaypalCheckout = function( value ){
          if( me.currencySymbol() == "&#36;" ){
            return "$" + parseFloat( value ).toFixed(2);
          } else {
            return "" + parseFloat(value ).toFixed(2);
          }
        };
        /******************************************************
﻿  ﻿  ﻿  ﻿  Formatting
﻿   ******************************************************/
        me.valueToCurrencyString = function( value ) {
          return parseFloat( value ).toCurrency( me.currencySymbol() );
        };
        me.valueToPercentageString = function( value ){
          return parseFloat( 100*value ) + "%";
        };
        me.valueToImageString = function( value ){
          if( value.match(/<\s*img.*src\=/) ){
            return value;
          } else {
            return "<img src=\"" + value + "\" />";
          }
        };
        me.valueToTextInput = function( value , html ){
          return "<input type=\"text\" value=\"" + value + "\" " + html + " />";
        };
        me.valueToLink = function( value, link, html){
          return "<a href=\"" + link + "\" " + html + " >" + value + "</a>";
        };
        me.valueToElement = function( type , value , html ){
          return "<" + type + " " + html + " > " + value + "</" + type + ">";
        };
        /******************************************************
﻿  ﻿  ﻿  ﻿  Duplicate management
﻿   ******************************************************/
        me.hasItem = function ( item ) {
          for( var current in me.items ) {
            var testItem = me.items[current];
            var matches = true;
            for( var field in item ){
              if( typeof( item[field] ) != "function"﻿  &&
                 field != "quantity"  ﻿  ﻿  ﻿  ﻿  &&
                 field != "id" ﻿  ﻿  ﻿  ﻿  ﻿  ﻿  ){
                if( item[field] != testItem[field] ){
                  matches = false;
                }
              }﻿  
            }
            if( matches ){
              return current;
            }
          }
          return false;
        };
        /******************************************************
﻿  ﻿  ﻿  ﻿  Cart Update managment
﻿   ******************************************************/
        me.update = function() {
          if( !simpleCart.isLoaded ){
            simpleCart.load();
          }
          if( !simpleCart.pageIsReady ){
            simpleCart.initializeView();
          }
          me.updateTotals();
          me.updateView();
          me.save();
        };
        me.updateTotals = function() {
          me.total = 0 ;
          me.quantity  = 0;
          for( var current in me.items ){
            var item = me.items[current];
            if( item.quantity < 1 ){
              item.remove();
            } else if( item.quantity !== null && item.quantity != "undefined" ){
              me.quantity = parseInt(me.quantity,10) + parseInt(item.quantity,10);
            }
            if( item.price ){
              me.total = parseFloat(me.total) + parseInt(item.quantity,10)*parseFloat(item.price);
            }
          }
          me.shippingCost = me.shipping();
          me.taxCost = parseFloat(me.total)*me.taxRate;
          me.finalTotal = me.shippingCost + me.taxCost + me.total - me.couponcode;
          if (me.finalTotal <= 0) {
            me.finalTotal = 0;﻿  
          }
        };
        me.applydiscount = function() {
          var ccode = document.getElementById('ccv').value;
          if (ccode == "abc123") {
            me.couponcode = 1;﻿  
            alert("Discount Applied");
          } else {
            me.couponcode = 0;﻿  
          }
          document.getElementById('couponcode').innerHTML = "Discount: $" + me.couponcode.toFixed(2) + " <br />";
          me.update();
        }
        me.shipping = function(){
          if( parseInt(me.quantity,10)===0 )
            return 0;
          var shipping = ﻿  parseFloat(me.shippingFlatRate) +
              parseFloat(me.shippingTotalRate)*parseFloat(me.total) +
              parseFloat(me.shippingQuantityRate)*parseInt(me.quantity,10),
              nextItem,
              next;
          for(next in me.items){
            nextItem = me.items[next];
            if( nextItem.shipping ){
              if( typeof nextItem.shipping == 'function' ){
                shipping += parseFloat(nextItem.shipping());
              } else {
                shipping += parseFloat(nextItem.shipping);
              }
            }
          }
          return shipping;
        }
        me.initialize = function() {
          simpleCart.initializeView();
          simpleCart.load();
          simpleCart.update();
        };
      }
      /********************************************************************************************************
 *﻿  ﻿  ﻿  Cart Item Object
 ********************************************************************************************************/
      function CartItem() {
        this.id = "c" + NextId++;
      }
      CartItem.prototype.set = function ( field , value ){
        field = field.toLowerCase();
        if( typeof( this[field] ) != "function" && field != "id" ){
          if( field == "quantity" ){
            value = value.replace( /[^(\d|\.)]*/gi , "" );
            value = value.replace(/,*/gi, "");
            value = parseInt(value,10);
          } else if( field == "price"){
            value = value.replace( /[^(\d|\.)]*/gi, "");
            value = value.replace(/,*/gi , "");
            value = parseFloat( value );
          }
          if( typeof(value) == "number" && isNaN( value ) ){
            error( "Improperly formatted input.");
          } else {
            this[field] = value;
            this.checkQuantityAndPrice();
          }﻿  ﻿  ﻿  
        } else {
          error( "Cannot change " + field + ", this is a reserved field.");
        }
        simpleCart.update();
      };
      CartItem.prototype.increment = function(){
        this.quantity = parseInt(this.quantity,10) + 1;
        simpleCart.update();
      };
      CartItem.prototype.decrement = function(){
        if( parseInt(this.quantity,10) < 2 ){
          this.remove();
        } else {
          this.quantity = parseInt(this.quantity,10) - 1;
          simpleCart.update();
        }
      };
      CartItem.prototype.print = function () {
        var returnString = '';
        for( var field in this ) {
          if( typeof( this[field] ) != "function" ) {
            returnString+= escape(field) + "=" + escape(this[field]) + "||";
          }
        }
        return returnString.substring(0,returnString.length-2);
      };
      CartItem.prototype.checkQuantityAndPrice = function() {
        if( !this.price || this.quantity == null || this.quantity == 'undefined'){
          this.quantity = 1;
          error('No quantity for item.');
        } else {
          this.quantity = ("" + this.quantity).replace(/,*/gi, "" );
          this.quantity = parseInt( ("" + this.quantity).replace( /[^(\d|\.)]*/gi, "") , 10);
          if( isNaN(this.quantity) ){
            error('Quantity is not a number.');
            this.quantity = 1;
          }
        }
        if( !this.price || this.price == null || this.price == 'undefined'){
          this.price=0.00;
          error('No price for item or price not properly formatted.');
        } else {
          this.price = ("" + this.price).replace(/,*/gi, "" );
          this.price = parseFloat( ("" + this.price).replace( /[^(\d|\.)]*/gi, "") );
          if( isNaN(this.price) ){
            error('Price is not a number.');
            this.price = 0.00;
          }
        }
      };
      CartItem.prototype.parseValuesFromArray = function( array ) {
        if( array && array.length && array.length > 0) {
          for(var x=0, xlen=array.length; x<xlen;x++ ){
            /* ensure the pair does not have key delimeters */
            array[x].replace(/||/, "| |");
            array[x].replace(/\+\+/, "+ +");
            /* split the pair and save the unescaped values to the item */
            var value = array[x].split('=');
            if( value.length>1 ){
              if( value.length>2 ){
                for(var j=2, jlen=value.length;j<jlen;j++){
                  value[1] = value[1] + "=" + value[j];
                }
              }
              this[ unescape(value[0]).toLowerCase() ] = unescape(value[1]);
            }
          }
          return true;
        } else {
          return false;
        }
      };
      CartItem.prototype.remove = function() {
        simpleCart.remove(this.id);
        simpleCart.update();
      };
      /********************************************************************************************************
 *﻿  ﻿  ﻿  Shelf Object for managing items on shelf that can be added to cart
 ********************************************************************************************************/
      function Shelf(){
        this.items = {};
      }﻿  
      Shelf.prototype.readPage = function () {
        this.items = {};
        var newItems = getElementsByClassName( "simpleCart_shelfItem" );
        for( var current in newItems ){
          var newItem = new ShelfItem();
          this.checkChildren( newItems[current] , newItem );
          this.items[newItem.id] = newItem;
        }
      };
      Shelf.prototype.checkChildren = function ( item , newItem) {
        for(var x=0;item.childNodes[x];x++){
          var node = item.childNodes[x];
          if( node.className && node.className.match(/item_[^ ]+/) ){
            var data = /item_[^ ]+/.exec(node.className)[0].split("_");
            if( data[1] == "add" || data[1] == "Add" ){
              var tempArray = [];
              tempArray.push( node );
              var addFunction = simpleCart.Shelf.addToCart(newItem.id);
              simpleCart.addEventToArray( tempArray , addFunction , "click");
              node.id = newItem.id;
            } else {
              newItem[data[1]]  = node;
            }
          }﻿  ﻿  
          if( node.childNodes[0] ){
            this.checkChildren( node , newItem );﻿  
          }﻿  
        }
      };
      Shelf.prototype.empty = function () {
        this.items = {};
      };
      Shelf.prototype.addToCart = function ( id ) {
        return function(){
          if( simpleCart.Shelf.items[id]){
            simpleCart.Shelf.items[id].addToCart();
          } else {
            error( "Shelf item with id of " + id + " does not exist.");
          }
        };
      };
      /********************************************************************************************************
 *﻿  ﻿  ﻿  Shelf Item Object
 ********************************************************************************************************/
      function ShelfItem(){
        this.id = "s" + NextId++;
      }﻿  
      ShelfItem.prototype.remove = function () {
        simpleCart.Shelf.items[this.id] = null;
      };
      ShelfItem.prototype.addToCart = function () {
        var outStrings = [],valueString;
        for( var field in this ){
          if( typeof( this[field] ) != "function" && field != "id" ){
            valueString = "";
            switch(field){
              case "price":
                if( this[field].value ){
                  valueString = this[field].value;
                } else if( this[field].innerHTML ) {
                  valueString = this[field].innerHTML;
                }
                /* remove all characters from price except digits and a period */
                valueString = valueString.replace( /[^(\d|\.)]*/gi , "" );
                valueString = valueString.replace( /,*/ , "" );
                break;
              case "image":
                valueString = this[field].src;
                break;
              default:
                if( this[field].value ){
                  valueString = this[field].value;
                } else if( this[field].innerHTML ) {
                  valueString = this[field].innerHTML;
                } else if( this[field].src ){
                  valueString = this[field].src;
                } else {
                  valueString = this[field];
                }
                break;
            }
            outStrings.push( field + "=" + valueString );
          }
        }
        simpleCart.add( outStrings );
      };
      /********************************************************************************************************
 * Thanks to Peter-Paul Koch for these cookie functions (http://www.quirksmode.org/js/cookies.html)
 ********************************************************************************************************/
      function createCookie(name,value,days) {
        if (days) {
          var date = new Date();
          date.setTime(date.getTime()+(days*24*60*60*1000));
          var expires = "; expires="+date.toGMTString();
        }
        else var expires = "";
        document.cookie = name+"="+value+expires+"; path=/";
      }
      function readCookie(name) {
        var nameEQ = name + "=";
        var ca = document.cookie.split(';');
        for(var i=0;i < ca.length;i++) {
          var c = ca[i];
          while (c.charAt(0)==' ') c = c.substring(1,c.length);
          if (c.indexOf(nameEQ) === 0) return c.substring(nameEQ.length,c.length);
        }
        return null;
      }
      function eraseCookie(name) {
        createCookie(name,"",-1);
      }
      //*************************************************************************************************
      /*
﻿  Developed by Robert Nyman, http://www.robertnyman.com
﻿  Code/licensing: http://code.google.com/p/getelementsbyclassname/
*/﻿  
      var getElementsByClassName = function (className, tag, elm){
        if (document.getElementsByClassName) {
          getElementsByClassName = function (className, tag, elm) {
            elm = elm || document;
            var elements = elm.getElementsByClassName(className),
                nodeName = (tag)? new RegExp("\\b" + tag + "\\b", "i") : null,
                returnElements = [],
                current;
            for(var i=0, il=elements.length; i<il; i+=1){
              current = elements[i];
              if(!nodeName || nodeName.test(current.nodeName)) {
                returnElements.push(current);
              }
            }
            return returnElements;
          };
        }
        else if (document.evaluate) {
          getElementsByClassName = function (className, tag, elm) {
            tag = tag || "*";
            elm = elm || document;
            var classes = className.split(" "),
                classesToCheck = "",
                xhtmlNamespace = "http://www.w3.org/1999/xhtml",
                namespaceResolver = (document.documentElement.namespaceURI === xhtmlNamespace)? xhtmlNamespace : null,
                returnElements = [],
                elements,
                node;
            for(var j=0, jl=classes.length; j<jl; j+=1){
              classesToCheck += "[contains(concat(' ', @class, ' '), ' " + classes[j] + " ')]";
            }
            try﻿  {
              elements = document.evaluate(".//" + tag + classesToCheck, elm, namespaceResolver, 0, null);
            }
            catch (e) {
              elements = document.evaluate(".//" + tag + classesToCheck, elm, null, 0, null);
            }
            while ((node = elements.iterateNext())) {
              returnElements.push(node);
            }
            return returnElements;
          };
        }
          else {
            getElementsByClassName = function (className, tag, elm) {
              tag = tag || "*";
              elm = elm || document;
              var classes = className.split(" "),
                  classesToCheck = [],
                  elements = (tag === "*" && elm.all)? elm.all : elm.getElementsByTagName(tag),
                  current,
                  returnElements = [],
                  match;
              for(var k=0, kl=classes.length; k<kl; k+=1){
                classesToCheck.push(new RegExp("(^|\\s)" + classes[k] + "(\\s|$)"));
              }
              for(var l=0, ll=elements.length; l<ll; l+=1){
                current = elements[l];
                match = false;
                for(var m=0, ml=classesToCheck.length; m<ml; m+=1){
                  match = classesToCheck[m].test(current.className);
                  if (!match) {
                    break;
                  }
                }
                if (match) {
                  returnElements.push(current);
                }
              }
              return returnElements;
            };
          }
        return getElementsByClassName(className, tag, elm);
      };
